package com.strings;

public class ImmutableString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Sumanth";  
		   s.concat(" Savanapelly");//concat() method appends the string at the end  
		   System.out.println(s);//will print Sumanth because strings are immutable objects  
		 
	}

}
