package com.strings;

public class StringConcatenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//using +(Stringconcatenation)operator
		 String s="Sumanth"+" Savanapelly";
		 System.out.println(s);//Sumanth Savanapelly
		 
		//Using concat()method
		 String s1="Sumanth ";
		 String s2="Savanapelly";
		 String s3=s1.concat(s2);
		 System.out.println(s3);//Sumanth Savanapelly
	}

}
